import java.util.*;
public class Switch_case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       Scanner scan = new Scanner(System.in);
       int r;
      String n=scan.next();
       int a=scan.nextInt();
       int b=scan.nextInt();
       char op=scan.next().charAt(1);
       switch(op) {
    	   case '+':
    		   r=a+b;
    		   System.out.println(r);
    		   break;
	}

}}

